import React from 'react'
import ReactDOM from 'react-dom/client'
import JSONDATA from './MOCK_DATA.json'
import {UseState} from 'react'

class AccordionMenu extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        contentVisible: true
      };

      // bind event handler methods here (defined below) for callback to work
      this.toggleContentVisible = this.toggleContentVisible.bind(this);
    }
  
     myFunction=(myFunction)=> {
      document.getElementById("demo").innerHTML = "Hello World";
    }



  counter = (counter) => {
    this.setState((prevState) => ({
      [counter]: prevState[counter] ? prevState[counter] + 1 : 1,
    }));
  };

  
    toggleContentVisible() {
      // called at onClick of accordion button
      // toggles contentVisible between true and false
      this.setState((prevState) => {
        return { contentVisible: !prevState.contentVisible };
      });
    }
  
    render() {
      const [searchTerm, setSearchTerm] = UseState('')
      return (
        <div className="accordion_container">
          <div className="accordion">
            {/* when button clicked, toggle the contentVisible value in our state*/}
            <button
              type="button"
              className="accordion_title"
              onClick={this.toggleContentVisible}
            >
              Accordion Menu
            </button>
          </div>
          {/* For the below Div hide / show:
              Using string literals, and a ternary if check on our states contentVisible value,
              we append the .show_content CSS class if contentVisible === true
              else we append the empty string if contentVisible === false
              Note: We are just combining multiple CSS classes here to create different effects
              Same as how we use CSS class combination in regular web pages to create visuals.
          */}
          <div
            className={`accordion_content ${
              this.state.contentVisible ? "show_content" : ""
            }`}
          >
            <div className="bg-[url('https://picsum.photos/200/300')]">
 
                <h1>Main food</h1>
                
                <ul className='12' id='myUL'>
                  

                  {JSONDATA.filter((val)=>{
                    if(searchTerm === ""){
                      return val;
                    }else if(val.first_name.toLowerCase().includes(searchTerm.toLowerCase())){
                      return val;
                    }
                  }).map((val,key)=>{
                  return (
                  <li className='user inline-flex m-[15px]' key={key}>
                    <div className="rounded-md h-[200px] w-[300px]  bg-[url('./asset/image/hamburger.png')] flex flex-col space-y-12 hover:bg-blue-600 transition duration-500 hover:scale-105" >
                        <div className="text-3xl backdrop-blur-sm">{val.first_name}</div>
                        <div className="bg-yellow-400 w-[150px] rounded-3xl ml-[75px]">
                          peper, onion, bread
                        </div>
 
                        <div className="flex space-x-20 content-center ml-[70px]">
                          <button className="text-xl" onClick={() => this.counter(1)}>
                            ❤️{this.state[1] ? this.state[1] : 0} 
                          </button>
                          <div className="text-3xl">21$</div>
                        </div>       
                    </div>
                  </li>
                  )
                  })}
    
                </ul>  
            </div>

          </div>

        </div>
      );
    }
  }

  export default AccordionMenu;
